import { createContext } from "react";
import { useState, useEffect } from "react";
import axios from "axios";

export const AuthenticationContext = createContext();


export const AuthenticationContextProvider = ({ children }) => {
    const [currentUser, setCurrentUser] = useState(
        JSON.parse(localStorage.getItem("user")) || null);


    const login = async () => {
        const res = await axios.post("http://localhost:8800/api/auth/login", 
        inputs, {
            withCredentials: true,
        });

        setCurrentUser(res.data)
        // skip for now
        // setCurrentUser({
        //     id: 1,
        //     name: "Mary Green",
        //     profilePicture: "https://images.unsplash.com/photo-1558280417-ea782f829e93?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8OTZ8fGFzaWFuJTIwYmVhdXR5fGVufDB8fDB8fHww"
        // });

    };


    useEffect(() => {
        localStorage.setItem("user", JSON.stringify(currentUser))
    }, [currentUser]);


    return (
        <AuthenticationContext.Provider value={{ currentUser, login }}>
            {children}
        </AuthenticationContext.Provider>
    );
};
